"use strict";
const init = (modules) => {
    const _ts = modules.typescript;
    let tsLanguageService = null;
    /**
     * Returns a cached version of the TSLS.
     * Useful if other global or local plugins modify it.
     */
    const getTypeScriptLanguageService = () => tsLanguageService;
    const create = (info) => {
        tsLanguageService = info.languageService;
        return Object.assign(Object.assign({}, info.languageService), { getTypeScriptLanguageService });
    };
    return { create };
};
module.exports = init;
